            function manipulate_update_button($buttons, $rowData) { 
                //Load Javascript In Here 
                // if ($this->input->get('action') == 'view') {
                //     unset($buttons['update']);
                // }
                // else{ 
                    
                // }
                
                // return $buttons;

                if ($this->input->get('action') == 'view') {
                    unset($buttons['update_back']);
                    unset($buttons['update']);
                }
                else{ 
                    $cNip= $this->user->gNIP;
            
                    $js = $this->load->view('export/js/export_req_refor_js');  
                    $update = '<button onclick="javascript:update_btn_back(\'export_req_refor\', \''.base_url().'processor/reformulasi/export/req/refor?company_id='.$this->input->get('company_id').'&group_id='.$this->input->get('group_id').'&modul_id='.$this->input->get('modul_id').'\', this)" class="ui-button-text icon-save" id="button_update_export_req_refor">Update & Submit</button>';
                    $updatedraft = '<button onclick="javascript:update_draft_btn(\'export_req_refor\', \''.base_url().'processor/reformulasi/export/req/refor?company_id='.$this->input->get('company_id').'&draft=true&group_id='.$this->input->get('group_id').'&modul_id='.$this->input->get('modul_id').'\', this, true)" class="ui-button-text icon-save" id="button_save_export_req_refor">Update as Draft</button>';

                    $approve = '<button onclick="javascript:load_popup(\''.base_url().'processor/reformulasi/export/req/refor?action=approve&iexport_req_refor='.$rowData['iexport_req_refor'].'&cNip='.$cNip.'&lvl=1&status=1&group_id='.$this->input->get('group_id').'&modul_id='.$this->input->get('modul_id').'\')" class="ui-button-text icon-save" id="button_approve_export_req_refor">Approve</button>';
                    $reject = '<button onclick="javascript:load_popup(\''.base_url().'processor/reformulasi/export/req/refor?action=reject&iexport_req_refor='.$rowData['iexport_req_refor'].'&cNip='.$cNip.'&lvl=1&status=2&group_id='.$this->input->get('group_id').'&modul_id='.$this->input->get('modul_id').'\')" class="ui-button-text icon-save" id="button_reject_export_req_refor">Reject</button>';


                    if($rowData['iSubmit']==0){
                        $buttons['update'] = $updatedraft.$update.$js;  
                    }else{
                        if($rowData['iApproval_ats_inisiator']==0 or empty($rowData['iApproval_ats_inisiator'])){
                            if($this->upperInisiator($rowData['cInisiator_export'],$cNip)==1){
                                $buttons['update'] = $approve.$reject.$js;
                            }else{
                                unset($buttons['update_back']);
                                unset($buttons['update']);
                            }
                        }else{
                            unset($buttons['update_back']);
                            unset($buttons['update']);
                        }
                        
                        
                    }

                }
                
                return $buttons;
            }
            function upperInisiator($cNip,$cUpper){
                $sql = "SELECT e.`cUpper` FROM hrd.`employee` e WHERE e.`cNip` = '".$cNip."' LIMIT 1";
                $dt = $this->db->query($sql)->row_array();
                if(!empty($dt['cUpper'])){
                    if($dt['cUpper']==$cUpper){
                        return 1;
                    }else{
                        return 0;
                    }
                }else{
                    return 0;
                }
            }
