                function approve_process() {
                    $post = $this->input->post();
                    $cNip= $this->user->gNIP;
                    $vName= $this->user->gName;
                    $iexport_refor_validasi_moa = $post['iexport_refor_validasi_moa'];
                    $vRemark = $post['vRemark'];
                    $lvl = $post['lvl'];

                    //Letakan Query Update approve disini

                    $this->db->where('iexport_refor_validasi_moa', $post['iexport_refor_validasi_moa']);
                    $this->db->update('reformulasi.export_refor_validasi_moa', array('iaprove'=>2,'caprove'=>$cNip,'daprove'=>date('Y-m-d H:i:s'),'taprove'=>$vRemark));


                    $data['status']  = true;
                    $data['last_id'] = $post['iexport_refor_validasi_moa'];
                    return json_encode($data);
                }




