                    $this->db->update('reformulasi.export_refor_validasi_moa', array('iaprove'=>2,'caprove'=>$cNip,'daprove'=>date('Y-m-d H:i:s'),'taprove'=>$vRemark));


                    $data['status']  = true;
                    $data['last_id'] = $post['iexport_refor_validasi_moa'];
                    return json_encode($data);
                }



