            function manipulate_update_button($buttons, $rowData) {
                //Load Javascript In Here
               if ($this->input->get('action') == 'view') {
                    unset($buttons['update_back']);
                    unset($buttons['update']);
                }
                else{
                    $cNip= $this->user->gNIP;

                    $js = $this->load->view('export/js/export_validasi_moa_js');
                    $update = '<button onclick="javascript:update_btn_back(\'export_validasi_moa\', \''.base_url().'processor/reformulasi/export/validasi/moa?company_id='.$this->input->get('company_id').'&group_id='.$this->input->get('group_id').'&modul_id='.$this->input->get('modul_id').'\', this)" class="ui-button-text icon-save" id="button_update_export_validasi_moa">Update & Submit</button>';
                    $updatedraft = '<button onclick="javascript:update_draft_btn(\'export_validasi_moa\', \''.base_url().'processor/reformulasi/export/validasi/moa?company_id='.$this->input->get('company_id').'&draft=true&group_id='.$this->input->get('group_id').'&modul_id='.$this->input->get('modul_id').'\', this, true)" class="ui-button-text icon-save" id="button_save_export_validasi_moa">Update as Draft</button>';

                    $approve = '<button onclick="javascript:load_popup(\''.base_url().'processor/reformulasi/export/validasi/moa?action=approve&iexport_refor_validasi_moa='.$rowData['iexport_refor_validasi_moa'].'&cNip='.$cNip.'&lvl=1&status=1&group_id='.$this->input->get('group_id').'&modul_id='.$this->input->get('modul_id').'\')" class="ui-button-text icon-save" id="button_approve_export_validasi_moa">Approve</button>';
                    $reject = '<button onclick="javascript:load_popup(\''.base_url().'processor/reformulasi/export/validasi/moa?action=reject&iexport_refor_validasi_moa='.$rowData['iexport_refor_validasi_moa'].'&cNip='.$cNip.'&lvl=1&status=2&group_id='.$this->input->get('group_id').'&modul_id='.$this->input->get('modul_id').'\')" class="ui-button-text icon-save" id="button_reject_export_validasi_moa">Reject</button>';

                    if($rowData['isubmit']==0){
                        $buttons['update'] = $updatedraft.$update.$js;
                    }else{
                        if($rowData['iaprove']==0 or empty($rowData['iaprove'])){
                            if($this->auth_export->is_manager()){
                                $x=$this->auth_export->tipe();
                                $manager=$x['manager'];
                                if(in_array('AD', $manager)){
                                    ///$buttons['update'] = $approve.$reject.$js;
                                    $buttons['update'] = $approve.$js;
                                }else{
                                    unset($buttons['update_back']);
                                    unset($buttons['update']);
                                }
                            }
                        }else{
                            unset($buttons['update_back']);
                            unset($buttons['update']);
                        }

                    }

                }
                return $buttons;
            }

